var majid = majid || {};
(function () {

    /* Application paths *****************************************/

    // Current application root path (including virtual directory if exists).
    majid.appPath = majid.appPath || '/';

    /* AUTHORIZATION **********************************************/
    // Implements Authorization API that simplifies usage of authorization scripts generated by Majid.

    majid.auth = majid.auth || {};

    majid.auth.tokenCookieName = 'Majid.AuthToken';
    majid.auth.tokenHeaderName = 'Authorization';

    majid.auth.setToken = function (authToken, expireDate) {
        majid.utils.setCookieValue(majid.auth.tokenCookieName, authToken, expireDate, majid.appPath);
    };

    majid.auth.getToken = function () {
        return majid.utils.getCookieValue(majid.auth.tokenCookieName);
    }

    majid.auth.clearToken = function () {
        majid.auth.setToken();
    }

    /* UTILS ***************************************************/

    majid.utils = majid.utils || {};

    /**
         * Sets a cookie value for given key.
         * This is a simple implementation created to be used by MAJID.
         * Please use a complete cookie library if you need.
         * @param {string} key
         * @param {string} value
         * @param {Date} expireDate (optional). If not specified the cookie will expire at the end of session.
         * @param {string} path (optional)
         */
    majid.utils.setCookieValue = function (key, value, expireDate, path) {
        var cookieValue = encodeURIComponent(key) + '=';

        if (value) {
            cookieValue = cookieValue + encodeURIComponent(value);
        }

        if (expireDate) {
            cookieValue = cookieValue + "; expires=" + expireDate.toUTCString();
        }

        if (path) {
            cookieValue = cookieValue + "; path=" + path;
        }

        document.cookie = cookieValue;
    };

    /**
     * Gets a cookie with given key.
     * This is a simple implementation created to be used by MAJID.
     * Please use a complete cookie library if you need.
     * @param {string} key
     * @returns {string} Cookie value or null
     */
    majid.utils.getCookieValue = function (key) {
        var equalities = document.cookie.split('; ');
        for (var i = 0; i < equalities.length; i++) {
            if (!equalities[i]) {
                continue;
            }

            var splitted = equalities[i].split('=');
            if (splitted.length != 2) {
                continue;
            }

            if (decodeURIComponent(splitted[0]) === key) {
                return decodeURIComponent(splitted[1] || '');
            }
        }

        return null;
    };

    /**
     * Deletes cookie for given key.
     * This is a simple implementation created to be used by MAJID.
     * Please use a complete cookie library if you need.
     * @param {string} key
     * @param {string} path (optional)
     */
    majid.utils.deleteCookie = function (key, path) {
        var cookieValue = encodeURIComponent(key) + '=';

        cookieValue = cookieValue + "; expires=" + (new Date(new Date().getTime() - 86400000)).toUTCString();

        if (path) {
            cookieValue = cookieValue + "; path=" + path;
        }

        document.cookie = cookieValue;
    }

    /* SECURITY ***************************************/
    majid.security = majid.security || {};
    majid.security.antiForgery = majid.security.antiForgery || {};

    majid.security.antiForgery.tokenCookieName = 'XSRF-TOKEN';
    majid.security.antiForgery.tokenHeaderName = 'X-XSRF-TOKEN';

    majid.security.antiForgery.getToken = function () {
        return majid.utils.getCookieValue(majid.security.antiForgery.tokenCookieName);
    };

})();
